using System;

namespace ProjectName
{
    class Program
    {
        static void Main(string[] args)
        {
            Console.WriteLine("Hello, world!");
        }
    }
}

using Microsoft.AspNetCore.Mvc;

namespace ProjectName.Controllers
{
    [Route("/")]
    [ApiController]
    public class HomeController : ControllerBase
    {
        [HttpGet]
        public IActionResult Index()
        {
            return Ok("index");
        }
    }
}

namespace ProjectName.Services
{
    public class HomeService
    {
        public string GetMessage()
        {
            return "Hello from the service!";
        }
    }
}

namespace ProjectName.Repositories
{
    public class HomeRepository
    {
        public string FetchData()
        {
            return "Data from repository";
        }
    }
}

namespace ProjectName.Models
{
    public class HomeModel
    {
        public string Data { get; set; }
    }
}

using Xunit;
using ProjectName.Controllers;

namespace ProjectName.Tests.Controllers
{
    public class HomeControllerTest
    {
        [Fact]
        public void TestIndex()
        {
            var controller = new HomeController();
            var result = controller.Index() as OkObjectResult;
            Assert.Equal("index", result.Value);
        }
    }
}

using Xunit;
using ProjectName.Services;

namespace ProjectName.Tests.Services
{
    public class HomeServiceTest
    {
        [Fact]
        public void TestGetMessage()
        {
            var service = new HomeService();
            var message = service.GetMessage();
            Assert.Equal("Hello from the service!", message);
        }
    }
}

using Xunit;
using ProjectName.Repositories;

namespace ProjectName.Tests.Repositories
{
    public class HomeRepositoryTest
    {
        [Fact]
        public void TestFetchData()
        {
            var repository = new HomeRepository();
            var data = repository.FetchData();
            Assert.Equal("Data from repository", data);
        }
    }
}
